function makeNodeArray(rows, columns) {
  let initNode = [];
  for (let i = 0; i < rows; i++) {
    for (let j = 0; j < columns; j++) {
      initNode.push({ id: i + "_" + j, type: "EMPTY", callingnode: "" });
    }
  }
  return initNode;
}

const initState = {
  nodes: makeNodeArray(15, 20),
  isActive: {
    wall: false,
    startFinNode: false,
    algorithm: "Null",
    start: false
  }
};

const rootReducer = (state = initState, action) => {
  console.log(action);
  if (action.type === "WALL_STATUS_CHANGE") {
    let newWall = !state.isActive.wall;
    console.log(newWall);
    return {
      ...state,
      wall: newWall
    };
  }
  console.log(state.isActive.wall);
  return state;
};

export default rootReducer;
